import PySimpleGUI as sg
from pydub import AudioSegment
from glob import glob
import threading
#base64 animated gif
red_dots_ring = b''
showAnimation=False
aniMsg='Merging'
# pydub Module - https://github.com/jiaaro/pydub
# ffmpeg install how to - https://www.geeksforgeeks.org/how-to-install-ffmpeg-on-windows/
# glob Module - https://docs.python.org/3/library/glob.html
# https://www.pysimplegui.org/en/latest/#popup_get_file use for file browse, multifile browse and file save as
# https://www.pysimplegui.org/en/latest/#popup_get_folder use for folder browse
# https://www.pysimplegui.org/en/latest/cookbook/#threaded-long-operation
# https://www.pysimplegui.org/en/latest/#popup_animated

def merge(window, folder):
    window.write_event_value('start gif','')
    i=0
    book = AudioSegment.empty()
    chapters = [mp3_file for mp3_file in glob(folder+"/*.mp3")]
    for chapter in chapters:
        i+=1
        print (i)
        book+=AudioSegment.from_mp3(chapter)
    window.write_event_value('end gif','')
    window.write_event_value('merge done',book)

def saveMerge(window,book,saveFile):   
    window.write_event_value('start gif','')
    book.export(saveFile, format='MP3')
    window.write_event_value('end gif','')
    print(round(book.duration_seconds/60,2))
  
layout=[[sg.Input(key='folder',enable_events=True),sg.FolderBrowse()],
        [sg.Exit()]]

window=sg.Window('Find MP3 Merge Folder',layout)

while True:
    event,values= window.read(10)
    #print(event, values)
    if event == 'folder':
        threading.Thread(target=merge, args=(window,values['folder']), daemon=True).start() 
    if event == 'merge done':
        print (values)
        aniMsg='Saving...'
        saveFile= sg.popup_get_file('Merge Complete! Please Choose a Save Location',save_as=True)
        threading.Thread(target=saveMerge, args=(window,values['merge done'],saveFile), daemon=True).start()
    if event == 'start gif':
        print(event)
        showAnimation=True
    if event == 'end gif':
        print(event)
        showAnimation=False  
    if event in (sg.WIN_CLOSED, 'Exit'):
        break
    if showAnimation:
       sg.popup_animated(red_dots_ring,time_between_frames=100,message=aniMsg,font='Arial 32')
    else:
       sg.popup_animated(None)   
window.close()